namespace STARS.Applications.VETS.Plugins.ShiftTableCalculator
{
    [Export(typeof(ShiftTableCalculator)), PartCreationPolicy(CreationPolicy.Shared)]
    internal class ShiftTableCalculator
    {
        private readonly IDialogService _dialogService;
        private readonly IVETSEntityManagerView _entityManager;
        private readonly IEntityQuery _entityQuery;

        public Vehicle VehicleData { get; private set; }
        public Trace TraceData { get; private set; }
        public BoundVehicle BoundVehicleData { get; private set; }
        public string ShiftName;

        [ImportingConstructor]
        public ShiftTableCalculator(IDialogService dialogService, IVETSEntityManagerView entityManager, IEntityQuery entityQuery)
        {
            _dialogService = dialogService;
            _entityManager = entityManager;
            _entityQuery = entityQuery;
        }

        public void PerformCalculation(HomeViewModel homeViewModel)
        {
            if (IsPerformingCalculation) return;
            IsPerformingCalculation = true;

            if (!Directory.Exists(Config.LogFolder)) SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.LogFolderDoesNotExist, Config.LogFolder));

            VehicleData = homeViewModel.SelectedVehicle;
            BoundVehicleData = homeViewModel.BoundVehicleObject;
            ShiftName = homeViewModel.ShiftNames.ShiftName;

            List<double> pLoadMaxArray = new List<double>();
            List<string[]> cvsLinesArray = new List<string[]>();
            int shiftTableCount = _entityManager.ShiftTables.Value.Count();

            if (!homeViewModel.WmtcMode)
            {
                TraceData = homeViewModel.SelectedTrace;
                if (CalculateSingleShiftTable())
                {
                    pLoadMaxArray.Add(PloadMax);
                    cvsLinesArray.Add(cvsLines);
                }
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }
            else if (BoundVehicleData.Class == "2.1")
            {
                CalculateMultipleShiftTables(pLoadMaxArray, cvsLinesArray, new string[] { Config.Part1Reduced, Config.Part2Reduced }, 
                    new string[] { homeViewModel.ShiftNames.Part1Reduced, homeViewModel.ShiftNames.Part2Reduced } );
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }
            else if (BoundVehicleData.Class == "2.2")
            {
                CalculateMultipleShiftTables(pLoadMaxArray, cvsLinesArray, new string[] { Config.Part1, Config.Part2 },
                    new string[] { homeViewModel.ShiftNames.Part1, homeViewModel.ShiftNames.Part2 } );
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }
            else if (BoundVehicleData.Class == "3.1")
            {
                CalculateMultipleShiftTables(pLoadMaxArray, cvsLinesArray, new string[] { Config.Part1, Config.Part2, Config.Part3Reduced },
                    new string[] { homeViewModel.ShiftNames.Part1, homeViewModel.ShiftNames.Part2, homeViewModel.ShiftNames.Part3Reduced } );
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }
            else if (BoundVehicleData.Class == "3.2")
            {
                CalculateMultipleShiftTables(pLoadMaxArray, cvsLinesArray, new string[] { Config.Part1, Config.Part2, Config.Part3 },
                    new string[] { homeViewModel.ShiftNames.Part1, homeViewModel.ShiftNames.Part2, homeViewModel.ShiftNames.Part3 });
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }
            else
            {
                CalculateMultipleShiftTables(pLoadMaxArray, cvsLinesArray, new string[] {Config.Part1Reduced },
                    new string[] { homeViewModel.ShiftNames.Part1Reduced } );
                PostResourceCreation(pLoadMaxArray, cvsLinesArray, shiftTableCount);
            }

        }

        private void CalculateMultipleShiftTables(List<double> pLoadMaxArray, List<string[]> cvsLinesArray, string[] traceNames, string[] shiftNames)
        {
            if(traceNames.Length != shiftNames.Length)
            {
                SystemLogService.DisplayErrorInVETSLog("traceNames.Length != shiftNames.Length", Resources.CalculateShiftTable);
            }
            for(int i = 0; i < traceNames.Length; i++)
            {
                TraceData = _entityQuery.FirstOrDefault<Trace>(x => x.Name == traceNames[i]);
                ShiftName = shiftNames[i];
                if (CalculateSingleShiftTable())
                {
                    pLoadMaxArray.Add(PloadMax);
                    cvsLinesArray.Add(cvsLines);
                }
            }
        }

        private bool CalculateSingleShiftTable()
        {
            CheckValidityOfProperties();
            SetShiftData();
            SetTraceData();
            CalculateShiftData();
            CalculateVectorData();
            return CreateShiftTableResource();
        }

        private void PostResourceCreation(List<double> pLoadMaxArray, List<string[]> cvsLinesArray, int shiftTableCount)
        {
            if (pLoadMaxArray != null && pLoadMaxArray.Count > 0)
            {
                pLoadMaxArray.Reverse();
                cvsLinesArray.Reverse();
                Thread queryShiftTablesThread = new Thread(() => QueryMultipleShiftTablesOnLoop(shiftTableCount, pLoadMaxArray.Count, pLoadMaxArray, cvsLinesArray));
                queryShiftTablesThread.Start();
            }
            else
            {
                IsPerformingCalculation = false;
            }
        }

        #region Check Validity

        public void CheckValidityOfProperties()
        {
            if (VehicleData == null) SystemLogService.DisplayErrorInVETSLog(Resources.NullVehicleErrorMessage, Resources.DisplayName);
            if (TraceData == null) SystemLogService.DisplayErrorInVETSLog(Resources.NullTraceErrorMessage, Resources.DisplayName);
            if (ShiftName == null) SystemLogService.DisplayErrorInVETSLog(Resources.NullShiftNameErrorMessage, Resources.DisplayName);

            CheckIfValidNumberOfGears(BoundVehicleData.NumberOfGears, "Number of Gears");

            for (int i = 0; i < TypeCast.ToInt(BoundVehicleData.NumberOfGears); i++)
            {
                CheckIfNonNegativeNumber(BoundVehicleData.Gears[i], String.Format("Gear {0}", i + 1));
            }

            foreach (PropertyInfo property in BoundVehicleData.GetType().GetProperties())
            {
                if (!GenerateDisplayName(property.Name).Contains("Gear") && !GenerateDisplayName(property.Name).Contains("Class"))
                {
                    CheckIfNonNegativeNumber(property.GetValue(BoundVehicleData, null).ToString(), GenerateDisplayName(property.Name));
                }
            }

            CheckIfValidTraceData();
        }

        private void CheckIfValidNumberOfGears(string propertyVal, string displayName)
        {
            if (TypeCast.ToInt(propertyVal) < 3 || TypeCast.ToInt(propertyVal) > 6)
            {
                SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.InvalidNumberOfGearsErrorMessage, propertyVal, displayName), Resources.DisplayName);
            }
        }

        private void CheckIfNonNegativeNumber(string propertyVal, string displayName)
        {
            if (!TypeCast.IsDouble(propertyVal) || TypeCast.ToDouble(propertyVal) < 0)
            {
                SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.NonNegativeNumberErrorMessage, propertyVal, displayName), Resources.DisplayName);
            }
        }

        private void CheckIfValidTraceData()
        {
            if (TraceData.Vectors.Length < 1 || TraceData.Vectors[0].Data.Length < 1) SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.MissingTraceTimeVectorErrorMessage, TraceData.Name), Resources.DisplayName);
            if (TraceData.Vectors.Length < 2 || TraceData.Vectors[1].Data.Length < 1) SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.MissingTraceSpeedVectorErrorMessage, TraceData.Name), Resources.DisplayName);
            if (TraceData.Vectors[0].Data.Length != TraceData.Vectors[1].Data.Length) SystemLogService.DisplayErrorInVETSLog(String.Format(Resources.TraceTimeSpeedMismatchErrorMessage, TraceData.Name), Resources.DisplayName);
        }

        private string GenerateDisplayName(string propertyName)
        {
            string displayName = String.Empty;
            foreach (char c in propertyName)
            {
                if ((Char.IsUpper(c) || Char.IsDigit(c)) && displayName != String.Empty) displayName += " ";
                displayName += c;
            }
            return displayName;
        }

        #endregion

        #region Set Data

        private void SetShiftData()
        {
            EngineCapacity = TypeCast.ToDouble(BoundVehicleData.EngineCapacity);
            RatedPower = TypeCast.ToDouble(BoundVehicleData.RatedPower);
            KerbMass = TypeCast.ToDouble(BoundVehicleData.KerbMass);
            MaxSpeed = TypeCast.ToDouble(BoundVehicleData.MaxSpeed);
            RatedEngineSpeed = TypeCast.ToDouble(BoundVehicleData.RatedEngineSpeed);
            IdlingSpeed = TypeCast.ToDouble(BoundVehicleData.IdlingSpeed);
            GearCoeffs = new List<double>();
            NumberOfGears = TypeCast.ToInt(BoundVehicleData.NumberOfGears);
            for(int i = 0; i < NumberOfGears; i++)
            {
                GearCoeffs.Add(TypeCast.ToDouble(BoundVehicleData.Gears[i]));
            }        
        }

        private void SetTraceData()
        {
            _length = TypeCast.ToInt(TraceData.Vectors[0].Data[TraceData.Vectors[0].Data.Length - 1].ToString()) + 1;
            Time = new double[_length];
            VelocityMps = new double[_length];
            VelocityKph = new double[_length];
            Acceleration = new double[_length];
            Idle = new bool[_length];
            Accelerate = new bool[_length];
            Cruise = new bool[_length];
            Decelerate = new bool[_length];
            N = new double[_length];
            Nnorm = new double[_length];
            Pres = new double[_length];
            Pmax = new double[_length];
            Pload = new double[_length];
            Gear = new double[_length];
            Declutch = new bool[_length];

            if (_length == TraceData.Vectors[0].Data.Length)
            {
                for (int i = 0; i < _length; i++)
                {
                    Time[i] = i;
                    VelocityMps[i] = TypeCast.ToDouble(TraceData.Vectors[1].Data[i].ToString());
                }
            }
            else
            {
                int a = 0;
                double b = 0;
                double m = 0;          
                for (int i = 1; i < TraceData.Vectors[0].Data.Length; i++)
                {
                    a = TypeCast.ToInt(TraceData.Vectors[0].Data[i - 1].ToString());
                    b = TypeCast.ToDouble(TraceData.Vectors[1].Data[i - 1].ToString());
                    m = (TypeCast.ToDouble(TraceData.Vectors[1].Data[i].ToString()) - TypeCast.ToDouble(TraceData.Vectors[1].Data[i - 1].ToString())) / (TypeCast.ToDouble(TraceData.Vectors[0].Data[i].ToString()) - TypeCast.ToDouble(TraceData.Vectors[0].Data[i - 1].ToString()));
                    for(int x = 0; x < (TypeCast.ToInt(TraceData.Vectors[0].Data[i].ToString()) - TypeCast.ToInt(TraceData.Vectors[0].Data[i - 1].ToString())); x++)
                    {
                        Time[a + x] = a + x;
                        VelocityMps[a + x] = m * x + b;
                    }
                }
                Time[_length - 1] = _length - 1;
                VelocityMps[_length - 1] = VelocityMps[_length - 2] + m;
            }
        }

        #endregion

        #region Caclulate Data

        public void CalculateShiftData()
        {
            PowerToMassRatioIndex = (RatedPower / (KerbMass + 75)) * 1000;
            UpShiftVehicleSpeed = new List<double>();
            DownShiftVehicleSpeed = new List<double>();

            double firstUpGearMod = (0.5753 * Math.Exp(-1.9 * PowerToMassRatioIndex / 1000) - 0.1) * (RatedEngineSpeed - IdlingSpeed) + IdlingSpeed;
            double firstDownGearMod = (IdlingSpeed + 0.03 * (RatedEngineSpeed - IdlingSpeed));
            double gearMod = (0.5753 * Math.Exp(-1.9 * PowerToMassRatioIndex / 1000) * (RatedEngineSpeed - IdlingSpeed) + IdlingSpeed);

            for (int i = 0; i < GearCoeffs.Count - 1; i++)
            {
                if (i == 0)
                {
                    UpShiftVehicleSpeed.Add(firstUpGearMod / GearCoeffs[i]);
                    DownShiftVehicleSpeed.Add(firstDownGearMod / GearCoeffs[i + 1]);
                }
                else
                {
                    UpShiftVehicleSpeed.Add(gearMod / GearCoeffs[i]);
                    DownShiftVehicleSpeed.Add(UpShiftVehicleSpeed[i - 1]);
                }
            }
        }

        public void CalculateVectorData()
        {
            bool nonWMTC = true;

            if(TraceData.Name == Config.Part1)
            {
                Idle = IdlePart1;
                Accelerate = AcceleratePart1;
                Cruise = CruisePart1;
                Decelerate = DeceleratePart1;
                nonWMTC = false;
            }
            else if (TraceData.Name == Config.Part1Reduced)
            {
                Idle = IdlePart1Reduced;
                Accelerate = AcceleratePart1Reduced;
                Cruise = CruisePart1Reduced;
                Decelerate = DeceleratePart1Reduced;
                nonWMTC = false;
            }
            else if (TraceData.Name == Config.Part2)
            {
                Idle = IdlePart2;
                Accelerate = AcceleratePart2;
                Cruise = CruisePart2;
                Decelerate = DeceleratePart2;
                nonWMTC = false;
            }
            else if (TraceData.Name == Config.Part2Reduced)
            {
                Idle = IdlePart2Reduced;
                Accelerate = AcceleratePart2Reduced;
                Cruise = CruisePart2Reduced;
                Decelerate = DeceleratePart2Reduced;
                nonWMTC = false;
            }
            else if (TraceData.Name == Config.Part3)
            {
                Idle = IdlePart3;
                Accelerate = AcceleratePart3;
                Cruise = CruisePart3;
                Decelerate = DeceleratePart3;
                nonWMTC = false;
            }
            else if (TraceData.Name == Config.Part3Reduced)
            {
                Idle = IdlePart3Reduced;
                Accelerate = AcceleratePart3Reduced;
                Cruise = CruisePart3Reduced;
                Decelerate = DeceleratePart3Reduced;
                nonWMTC = false;
            }

            for (int i = 1; i < _length; i++)
            {
                VelocityKph[i] = VelocityMps[i] * 3.6;
                if (i == _length - 1) Acceleration[i] = 0;
                else Acceleration[i] = VelocityMps[i + 1] - VelocityMps[i];

                if (nonWMTC)
                {
                    if (Acceleration[i] >= 0.5 / 3.6) Accelerate[i] = true;
                    else if (Acceleration[i] <= -0.5 / 3.6) Decelerate[i] = true;
                    else if (VelocityMps[i] < 5 / 3.6) Idle[i] = true;
                    else Cruise[i] = true;
                }

                SetGear(i);
            }

            AdjustForAccelToDecelShifts();
            AdjustForDiscontinuousShifts();
            AdjustForIdleToFirst();
            AdjustForHighFrequencyShifts();
            CheckDriveability();
        }

        private void SetGear(int i)
        {
            if (Accelerate[i])
            {
                if (NumberOfGears > 5 && VelocityKph[i] > UpShiftVehicleSpeed[4]) Gear[i] = 6;
                else if (NumberOfGears > 4 && VelocityKph[i] > UpShiftVehicleSpeed[3]) Gear[i] = 5;
                else if (NumberOfGears > 3 && VelocityKph[i] > UpShiftVehicleSpeed[2]) Gear[i] = 4;
                else if (VelocityKph[i] > UpShiftVehicleSpeed[1]) Gear[i] = 3;
                else if (VelocityKph[i] > UpShiftVehicleSpeed[0]) Gear[i] = 2;
                else Gear[i] = 1;
            }
            else if (Cruise[i])
            {
                if (NumberOfGears > 5 && VelocityKph[i] > DownShiftVehicleSpeed[4]) Gear[i] = 6;
                else if (NumberOfGears > 4 && VelocityKph[i] > DownShiftVehicleSpeed[3]) Gear[i] = 5;
                else if (NumberOfGears > 3 && VelocityKph[i] > DownShiftVehicleSpeed[2]) Gear[i] = 4;
                else if (VelocityKph[i] > DownShiftVehicleSpeed[1]) Gear[i] = 3;
                else if (VelocityKph[i] > DownShiftVehicleSpeed[0]) Gear[i] = 2;
                else Gear[i] = 1;
            }
            else if (Decelerate[i])
            {
                if (NumberOfGears > 5 && VelocityKph[i] > DownShiftVehicleSpeed[4]) Gear[i] = 6;
                else if (NumberOfGears > 4 && VelocityKph[i] > DownShiftVehicleSpeed[3]) Gear[i] = 5;
                else if (NumberOfGears > 3 && VelocityKph[i] > DownShiftVehicleSpeed[2]) Gear[i] = 4;
                else if (VelocityKph[i] > DownShiftVehicleSpeed[1]) Gear[i] = 3;
                else if (VelocityKph[i] > DownShiftVehicleSpeed[0]) Gear[i] = 2;
                else Gear[i] = 1;
            }
            else Gear[i] = 0;
        }

        private void AdjustForDiscontinuousShifts()
        {
            for (int i = 1; i < _length - 1; i++)
            {
                if (Math.Abs(Gear[i] - Gear[i - 1]) > 1)
                {
                    if (Gear[i] > Gear[i - 1]) Gear[i] = Gear[i - 1] + 1;
                    else Gear[i] = Gear[i - 1] - 1;
                }
            }
        }

        private void AdjustForIdleToFirst()
        {
            for (int i = 4; i < _length - 1; i++)
            {
                if (Gear[i] == 0 && Gear[i + 1] == 1)
                {
                    Gear[i] = 1;
                    if (Gear[i - 1] == 0) Gear[i - 1] = 1;
                    if (Gear[i - 2] == 0) Gear[i - 2] = 1;
                    if (Gear[i - 3] == 0) Gear[i - 3] = 1;
                }
            }
        }

        private void AdjustForAccelToDecelShifts()
        {
            for (int i = 0; i < _length - 1; i++)
            {
                if (Accelerate[i])
                {
                    if (Decelerate[i + 1] && Gear[i] != Gear[i + 1])
                    {
                        KeepGear(ref i, Gear[i]);
                    }
                }
            }
        }

        private void KeepGear(ref int i, double keepGear)
        {
            i++;
            while (i < _length && Gear[i] != keepGear && Decelerate[i])
            {               
                Gear[i] = keepGear;
                i++;
            }
        }

        private void AdjustForHighFrequencyShifts()
        {
            for (int i = 1; i < _length - 1; i++)
            {
                if (Gear[i] != Gear[i - 1])
                {
                    if (Gear[i - 1] == Gear[i + 1])
                    {
                        Gear[i] = Gear[i - 1];
                    }
                }
            }

            for (int i = 1; i < _length - 2; i++)
            {
                if (Gear[i] != Gear[i - 1])
                {
                    if (Gear[i - 1] == Gear[i + 2])
                    {
                        Gear[i] = Gear[i - 1];
                        Gear[i + 1] = Gear[i - 1];
                    }
                }
            }

            for (int i = 1; i < _length - 3; i++)
            {
                if (Gear[i] != Gear[i - 1])
                {
                    if (Gear[i - 1] == Gear[i + 3])
                    {
                        Gear[i] = Gear[i - 1];
                        Gear[i + 1] = Gear[i - 1];
                        Gear[i + 2] = Gear[i - 1];
                    }
                }
            }

            for (int i = 1; i < _length - 4; i++)
            {
                if (Gear[i] != Gear[i - 1])
                {
                    if (Gear[i - 1] == Gear[i + 4])
                    {
                        Gear[i] = Gear[i - 1];
                        Gear[i + 1] = Gear[i - 1];
                        Gear[i + 2] = Gear[i - 1];
                        Gear[i + 3] = Gear[i - 1];
                    }
                }
            }
        }

        private void AdjustForSmoothTail()
        {
            if (_length - NumberOfGears < 0) return;
            for (int i = 0; i < NumberOfGears; i++)
            {
                if (Gear[_length - NumberOfGears + i] > NumberOfGears - 1 - i)
                {
                    Gear[_length - NumberOfGears + i] = NumberOfGears - 1 - i;
                }
            }
        }

        private void CheckDriveability()
        {
            PloadMax = 0;
            for (int i = 0; i < _length; i++)
            {
                if (Accelerate[i] || Cruise[i]) Pload[i] = Pres[i] / Pmax[i];

                if (Gear[i] == 0) N[i] = IdlingSpeed;
                else if (Gear[i] == 1) N[i] = VelocityKph[i] * GearCoeffs[0];
                else if (Gear[i] == 2) N[i] = VelocityKph[i] * GearCoeffs[1];
                else if (Gear[i] == 3) N[i] = VelocityKph[i] * GearCoeffs[2];
                else if (Gear[i] == 4) N[i] = VelocityKph[i] * GearCoeffs[3];
                else if (Gear[i] == 5) N[i] = VelocityKph[i] * GearCoeffs[4];
                else N[i] = VelocityKph[i] * GearCoeffs[5];

                if (N[i] < IdlingSpeed)
                {
                    N[i] = IdlingSpeed;
                    if (Gear[i] != 0 && Decelerate[i]) Declutch[i] = true;
                }
                Nnorm[i] = (N[i] - IdlingSpeed) / (RatedEngineSpeed - IdlingSpeed);

                Pres[i] = (AI * (KerbMass + 75) * VelocityKph[i] + (AJ * (KerbMass + 75) + AK) * Math.Pow(VelocityKph[i], 3) + AL * (KerbMass + 75) * Acceleration[i] * VelocityKph[i]) / 3600;
                Pmax[i] = (AB * Math.Pow(Nnorm[i], 6) + AC * Math.Pow(Nnorm[i], 5) + AD * Math.Pow(Nnorm[i], 4) + AE * Math.Pow(Nnorm[i], 3) + AF * Math.Pow(Nnorm[i], 2) + AG * Nnorm[i] + AH) * RatedPower;
                if ((Accelerate[i] || Cruise[i]) && Pmax[i] != 0) Pload[i] = Pres[i] / Pmax[i];
                if (Pload[i] > PloadMax) PloadMax = Pload[i];
            }
        }

        #endregion

        #region Create Shift Resource

        private bool CreateShiftTableResource()
        {
            if(PloadMax > 1)
            {
                if(_dialogService.PromptUser(
                    "Shift Table Calculator", 
                    String.Format("Drivability problems are to be expected for the vehicle/trace combination '{0}' and '{1}'." 
                    + Environment.NewLine + 
                    "Maximum p_load is {2}%. Create the shift table resource anyways?",
                    VehicleData.Name,
                    TraceData.Name,
                    Math.Round((PloadMax * 100), 2).ToString()), 
                    DialogIcon.Warning, DialogButton.Yes, 
                    DialogButton.Yes, DialogButton.No) == DialogButton.No)
                {
                    return false;
                }          
            }

            CreateEntity();
            GenerateCSVLines();
            return true;
        }

        private void CreateEntity()
        {
            ShiftTable newShiftTable = new ShiftTable();
            newShiftTable.TraceName = TraceData.Name;
            newShiftTable.MaximumGear = NumberOfGears;
            newShiftTable.TransmissionType = "Manual";
            newShiftTable.Name = ShiftName;
            SetShiftTableVectors(newShiftTable);
            _entityManager.ShiftTables.Value.Create(newShiftTable.Uri, newShiftTable.Properties);
        }

        private void SetShiftTableVectors(ShiftTable newShiftTable)
        {
            double currentGear = -1;
            bool currentDeclutch = false; ;
            List<double> timeVector = new List<double>();
            List<string> gearVector = new List<string>();
            List<bool> declutchVector = new List<bool>();
            List<string> messageVector = new List<string>();

            for (int i = 0; i < _length; i++)
            {
                if(Gear[i] != currentGear)
                {
                    currentGear = Gear[i];
                    currentDeclutch = Declutch[i];
                    timeVector.Add(TypeCast.ToDouble(i.ToString()));
                    if(Gear[i] == 0) gearVector.Add("Neutral");
                    else gearVector.Add(currentGear.ToString());
                    declutchVector.Add(currentDeclutch);
                    messageVector.Add(String.Empty);
                }
                else if (Declutch[i] != currentDeclutch)
                {
                    currentGear = Gear[i];
                    currentDeclutch = Declutch[i];
                    timeVector.Add(TypeCast.ToDouble(i.ToString()));
                    if (Gear[i] == 0) gearVector.Add("Neutral");
                    else gearVector.Add(currentGear.ToString());
                    declutchVector.Add(currentDeclutch);
                    messageVector.Add(String.Empty);
                }
            }

            newShiftTable.Vectors[0].Data = timeVector.Cast<object>().ToArray();
            newShiftTable.Vectors[1].Data = gearVector.Cast<object>().ToArray();
            newShiftTable.Vectors[2].Data = declutchVector.Cast<object>().ToArray();
            newShiftTable.Vectors[3].Data = messageVector.Cast<object>().ToArray();
        }

        private void QueryMultipleShiftTablesOnLoop(int resourceCount, int createCount, List<double> pLoadMaxArray, List<string[]> cvsLinesArray)
        {
            try
            {
                string errorMessage = String.Empty;
                string successMessage = String.Empty;
                string logMessage = String.Empty;
                string logPath = String.Empty;
                for (int i = 0; i < 100; i++)
                {
                    if (_entityManager.ShiftTables.Value.Count() == resourceCount + createCount)
                    {
                        var shiftTables = _entityManager.ShiftTables.Value.OrderByDescending(x => x.Created).ToList();
                        for (int j = createCount - 1; j >= 0; j--)
                        {
                            string newlyCreatedShiftTableName = shiftTables[j].Name;
                            ShiftTable newlyCreatedShiftTable = _entityQuery.FirstOrDefault<ShiftTable>(x => x.Name == newlyCreatedShiftTableName);

                            CustomFieldValue customField = newlyCreatedShiftTable.CustomFieldValues.FirstOrDefault(p => p.CustomFieldID == "PLoadMax");
                            if (customField == null)
                            {
                                _entityManager.ShiftTables.Value.Delete(newlyCreatedShiftTable.ID);
                                if (errorMessage == String.Empty) errorMessage += String.Format(Resources.CustomFeildDoesNotExistInShiftTableResourceErrorMessage, "PLoadMax");
                                else errorMessage += String.Format(Environment.NewLine + Resources.CustomFeildDoesNotExistInShiftTableResourceErrorMessage, "PLoadMax");
                            }
                            else
                            {
                                customField.Value = Math.Round((pLoadMaxArray[j] * 100), 2).ToString() + "%";
                                _entityManager.ShiftTables.Value.Update(newlyCreatedShiftTable.ID, newlyCreatedShiftTable.Properties);
                                logPath = SaveCVSFile(newlyCreatedShiftTableName, cvsLinesArray[j]);
                                if (successMessage == String.Empty) successMessage += String.Format("Shift table '{0}' has been successfully created.", newlyCreatedShiftTableName);
                                else successMessage += String.Format(Environment.NewLine + "Shift table '{0}' has been successfully created.", newlyCreatedShiftTableName);
                                if (logMessage == String.Empty) logMessage += String.Format("Log file: '{0}' was created.", logPath);
                                else logMessage += String.Format(Environment.NewLine + "Log file: '{0}' was created.", logPath);
                            }
                        }
                        if (errorMessage != String.Empty) SystemLogService.DisplayErrorInVETSLogNoReturn(errorMessage, Resources.DisplayName);
                        if (successMessage != String.Empty) SystemLogService.DisplayMessageInVETSLog(successMessage, Resources.DisplayName);
                        if (logMessage != String.Empty) SystemLogService.DisplayMessageInVETSLog(logMessage);
                        IsPerformingCalculation = false;
                        return;
                    }
                    Thread.Sleep(100);
                }
                SystemLogService.DisplayErrorInVETSLogNoReturn("Error creating shift table resource. Entity create timed out.");
                IsPerformingCalculation = false;
            }
            catch(Exception e)
            {
                IsPerformingCalculation = false;
                throw e;
            }
        }

