        private string SaveCVSFile(string shiftName, string[] cvsLines)
        {
            FileInfo[] logFiles = new DirectoryInfo(Config.LogFolder).GetFiles().OrderBy(x => x.CreationTime).ToArray();
            if (logFiles.Length > 499)
            {
                for(int i = 0; i < logFiles.Length - 499; i++)
                {
                    File.Delete(logFiles[i].FullName);
                }
            }

            DateTime now = DateTime.Now;
            string csvPath = Config.LogFolder +  now.Year + " " + now.ToString("MMMM", CultureInfo.InvariantCulture) + " " +
                             now.Day + " " + String.Format("{0:hh:mm:ss tt}", DateTime.Now).Replace(':', '_') + " " + shiftName + ".csv";
            cvsLines[0] = String.Format("Shift Table Name,{0},,", shiftName) + cvsLines[0];
            File.WriteAllLines(csvPath, cvsLines);
            return csvPath;
        }

